import React, { useState, useEffect } from "react";

// Array para almacenar los productos agregados
export const CartContext = React.createContext([]);

// CartComponentProvider es el proveedor de nuestro CartContext
export const CartComponentProvider = ({ children }) => {
  const [count, setCount] = useState(1);
  const [aggregateItem, setAggregateItem] = useState([]);

  const addItem = (smartwatch) => {
    const id = smartwatch.id;

    // Funci贸n para item duplicado
    const unDuplicateItem = () => {
      setAggregateItem([...aggregateItem, smartwatch]);
      setCount(count + 1);

      return console.log("Carrito actualizado", (aggregateItem, count));
    };

    // Condici贸n para determinar el item duplicado
    smartwatch.id != id ? unDuplicateItem() : alert("producto duplicado");
  };

  return (
    <CartContext.Provider
      value={{
        count,
        setCount,
        aggregateItem,
        setAggregateItem,
        addItem,
      }}
    >
      {/* Componentes que van a tener acceso */}
      {children}
    </CartContext.Provider>
  );
};
