import React, { useState, useEffect } from "react";
import Item from "./Item";

export const ItemList = (props) => {
  // Array con relojes
  const relojes = [
    {
      id: 1,
      title: "Smartwatch Samsung 1",
      price: 25000,
      pictureUrl: "../public/img/relojes/reloj-samsung-1.png",
      alt: "Smartwatch Samsung 1",
    },
    {
      id: 2,
      title: "Smartwatch Samsung 2",
      price: 25000,
      pictureUrl: "../public/img/relojes/reloj-samsung-2.png",
      alt: "Smartwatch Samsung 2",
    },
    {
      id: 3,
      title: "Smartwatch Apple watch 1",
      price: 25000,
      pictureUrl: "../public/img/relojes/apple-watch-1.png",
      alt: "Smartwatch Apple watch 1",
    },
  ];

  // Promise
  const promiseItem = () => {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        // Si est치 todo ok se muestra el resolve
        resolve(relojes);
      }, 2000);
    });
  };

  // Hook useState
  const [dataToShow, setDataToShow] = useState([]);

  // Funci칩n encargada de llamar a mi Promise
  /*   const ejecutarItem = () => {
    promiseItem().then(relojes) => {setDataToShow(relojes)};
  }; */

  /*   useEffect(() => {
    showProd().then((data) => {
      setProduct(data);
    });
  }, []); */

  // Hook useEffect (cada vez que se ejecuta este componente)
  useEffect(() => {
    promiseItem().then((relojes) => {
      setDataToShow(relojes);
    });
  }, []);

  return (
    <>
      {dataToShow.length == 0 ? (
        <h1>Cargando...</h1>
      ) : (
        <ul>
          {dataToShow.map((reloj) => (
            <Item
              key={reloj.id}
              title={reloj.title}
              price={reloj.price}
              pictureUrl={reloj.pictureUrl}
              alt={reloj.alt}
            />
          ))}
        </ul>
      )}
    </>
  );
};

export default ItemList;
