import React, { useState } from "react";

// Array para almacenar los productos agregados
export const CartContext = React.createContext([]);

// CartComponentProvider es el proveedor de nuestro CartContext
export const CartComponentProvider = ({ children }) => {
  // Cantidad de los productos agregados
  const [quantity, setQuantity] = useState(0);
  const [aggregateItems, setAggregateItems] = useState([]);

  const addItem = (smartwatch, itemCount) => {
    const id = smartwatch.itemToShow;
    const dupAggregateItems = [...aggregateItems];

    for (let i = 0; i < dupAggregateItems.length; i++) {
      console.log(id);
      console.log(dupAggregateItems[i].itemToShow);
      // comparar en el if si el item en dupAggregateItems[i] tiene el mismo id
      // que el item que llega en smartwatch
      // ** reemplazar condicion por la verdadera condicion. **
      if (dupAggregateItems[i] === id) {
        /* console.log("Entró"); */
        // si es el mismo, incrementas itemCount dentro de DupAggregateItems
        /**
         * escribí tu codigo aca
         */
        setAggregateItems(dupAggregateItems);
        setQuantity(quantity + itemCount);
        return;
      }
    }

    setAggregateItems([...aggregateItems, { smartwatch, itemCount }]);
    setQuantity(quantity + itemCount);
  };

  return (
    <CartContext.Provider
      value={{
        aggregateItems,
        setAggregateItems,
        addItem,
        quantity,
        setQuantity,
      }}
    >
      {/* Componentes que van a tener acceso */}
      {children}
    </CartContext.Provider>
  );
};
