import React, { useState } from "react";
import { database } from "../../firebase/firebase";
import firebase from "firebase/app";
import "firebase/firestore";

export const Form = ({ aggregateItems, itemsPrice, clear }) => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [mail, setMail] = useState("");
  const [redirect, setRedirect] = useState(false);
  const [orderNumber, setOrderNumber] = useState("");

  const createOrder = (e) => {
    e.preventDefault();

    const itemsToUpdate = dataBase.collection("items").where(
      firebase.firestore.FieldPath.documentId(),
      "in",
      items.map((i) => i.item.id)
    );

    itemsToUpdate.get().then((querySnapshot) => {
      const batch = dataBase.batch();
      const outOfStock = [];

      querySnapshot.docs.forEach((docSnapshot, idx) => {
        if (docSnapshot.data().stock >= items[idx].quantity) {
          batch.update(docSnapshot.ref, {
            stock: docSnapshot.data().stock - items[idx].quantity,
          });
        } else {
          outOfStock.push({ ...docSnapshot.data(), id: docSnapshot.id });
        }
      });

      if (outOfStock.length === 0) {
        const newOrderReference = dataBase.collection("orders").doc();
        batch.set(newOrderReference, {
          date: firebase.firestore.Timestamp.fromDate(new Date()),
        });
        batch.commit().then(() => {
          dataBase
            .collection("orders")
            // .orderBy('date', "asc").limitToLast(1)
            .orderBy("date", "desc")
            .limit(1)
            .get()
            .then((querySnapshot) => console.log(querySnapshot.docs[0].id));
        });
      }
    });
  };

  return (
    <>
      {redirect ? (
        alert(`Orden realizada con Ã©xito ${orderNumber}`)
      ) : (
        <form onSubmit={createOrder}>
          <p>
            <label>Nombre:</label>
            <br></br>
            <input
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Tomas Martinez"
              id="name"
              type="text"
              autoComplete="name"
              enterKeyHint="next"
              required
              autoFocus
            />
          </p>
          <p>
            <label>TelÃ©fono:</label>
            <br></br>
            <input
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              placeholder="+54 261 542389"
              id="phone"
              type="tel"
              autoComplete="phone"
              enterKeyHint="next"
            />
          </p>
          <p>
            <label>Mail:</label>
            <br></br>
            <input
              value={mail}
              onChange={(e) => setMail(e.target.value)}
              placeholder="ejemplo@ejemplo.com"
              id="mail"
              type="email"
              autoComplete="email"
              enterKeyHint="next"
            />
          </p>
          <button
            className="btn btn-outline-success"
            type="submit"
            disabled={name === "" || phone === "" || mail === ""}
          >
            Finalizar Compra
          </button>
        </form>
      )}
    </>
  );
};
