import React from "react";
import Item from "./Item";
import ItemCount from "../ItemCount";
import { Link } from "react-router-dom";

export const ItemDetail = (props) => {
  const smartwatches = props;
  const [visible, setVisible] = useState(true);

  return (
    <>
      <div className="row">
        <div className="smartwatches-container">
          <Item {...smartwatches} />

          <div className="addCart">
            <ItemCount stock={5} initial={1} />
            {/* <button className="btn" onClick={() => addCart()}>
              Agregar al carrito
            </button> */}
            <button className="btn">Agregar al carrito </button>

            {/* PREGUNTAR
            <h6>Llevás {ItemCount.count} productos en el carrito</h6> */}

            {/* <Link to="/">Volver atrás</Link> */}
          </div>

          <div className="">
            {visible ? (
              <>
                <button
                  className="agregarCarrito"
                  onClick={() => setVisible(false)}
                >
                  AGREGAR AL CARRITO!
                </button>
                <ItemCount stock={5} initial={1} />
              </>
            ) : (
              <div className="flex__gap-2">
                <button
                  className="cancelarCarrito"
                  onClick={() => setVisible(true)}
                >
                  CANCELAR
                </button>
                <button className="confirmarCarrito">CONFIRMAR</button>
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default ItemDetail;
