    <>
      {watchesToShow.length === 0 ? (
        <h3>Cargando...</h3>
      ) : (
        // Recorrer y mostrar array con relojes
        <div className="row">
          <div className="smartwatches-container">
            {watchesToShow.map((smartwatch) => (
              <Item item={item} />
            ))}
          </div>
        </div>
      )}
    </>
  );
};

export default ItemList;
